import React from 'react'
import { View, StyleSheet, Text, Image  } from 'react-native'

export const CurrentEvent = props => {
    return (
        <View style={styles.container}>
            <View style={styles.event}>
                <View style={{alignItems: "center", paddingTop:40}}>
                    <Image style={styles.avatar}
                           source={require('../img/event.png')}
                           style={{ width: 120, height: 120}}
                    />
                    <Text style={styles.name}>{props.name}</Text>
                    <Text style={styles.dateEvent}>{props.date} • {props.time} • {props.type}</Text>
                    <Text style={styles.dateEvent}>{props.city} • {props.place}</Text>
                    <TouchableOpacity style={styles.buttonDone} activeOpacity={0.5}>
                        <Image
                            source={require('../img/done.png')}
                            style={styles.buttonImageIconStyle}
                        />
                        <View style={styles.buttonIconSeparatorStyle} />
                        <Text style={styles.buttonTextStyleBlack}>Google</Text>
                    </TouchableOpacity>
                </View>
            </View>
        </View>

    )
}

const styles = StyleSheet.create ({

    container: {
        height: '100%',
        width: '100%',

    },
    event:{
        height: 250,
        width: '100%',
        shadowColor: '#e6e6e6',
        shadowOffset: {
            width: 0,
            height: 6,
        },
        shadowOpacity: 0.39,
        shadowRadius: 8.30,

        elevation: 13
    },
    avatar:{
        alignItems: 'center',
        justifyContent: 'center'
    },
    name: {
        fontFamily: 'Roboto',
        fontStyle: 'normal',
        fontSize: 24,
        lineHeight: 35,
        paddingTop:5,
        paddingBottom:5
    },
    dateEvent: {
        alignItems: 'center',
        fontFamily: 'Roboto',
        fontStyle: 'normal',
        fontSize: 17,
        lineHeight: 21,
    },
    rate: {
        color: '#E15241'
    },
    subTitle: {
        height: 300,
        width: '100%',
        flex: 1,
        alignItems: 'flex-start',
        justifyContent: 'center',
        lineHeight: 28,
        paddingTop: 15,
        paddingLeft:20,
        paddingBottom: 20
    },
    subTitleText: {
        fontFamily: 'Roboto',
        fontStyle: 'normal',
        fontSize: 24
    },
    items:{
        height: 300,
        width: '100%',
        flex: 1,
        alignItems: 'flex-start',
        justifyContent: 'center',
        lineHeight: 28,
        paddingLeft:20,
        paddingBottom: 5,
        paddingTop: 50

    },
    itemTitle:{
        fontFamily: 'Roboto',
        fontStyle: 'normal',
        fontSize: 24
    },
    itemResult:{
        fontFamily: 'Roboto',
        fontStyle: 'normal',
        fontSize: 14
    },
    itemRate: {
        fontFamily: 'Roboto',
        fontStyle: 'normal',
        fontSize: 14,
        color: '#14B571',
        paddingLeft: 280,
        paddingBottom: 10
    }
    

})
